let input = [
    '',
    '',
    '',
    '',
    '',
];

let codeMap = {
    '0,2': '1',
    '-1,1': '2',
    '0,1': '3',
    '1,1': '4',
    '-2,0': '5',
    '-1,0': '6',
    '0,0': '7',
    '1,0': '8',
    '2,0': '9',
    '-1,-1': 'A',
    '0,-1': 'B',
    '1,-1': 'C',
    '0,-2': 'D',
};

let current = {x: -2, y: 0};

let directions = {
    U: (current) => {
        if ((current.x === 0 && current.y < 2) || ([-1, 1].includes(current.x) && current.y < 1)) {
            current.y++;
        }
    },
    D: (current) => {
        if ((current.x === 0 && current.y > -2) || ([-1, 1].includes(current.x) && current.y > -1)) {
            current.y--;
        }
    },
    R: (current) => {
        if ((current.y === 0 && current.x < 2) || ([-1, 1].includes(current.y) && current.x < 1)) {
            current.x++;
        }
    },
    L: (current) => {
        if ((current.y === 0 && current.x > -2) || ([-1, 1].includes(current.y) && current.x > -1)) {
            current.x--;
        }
    },
};

let code = '';

input.forEach(function (inputLine) {
    let commands = inputLine.split('');
    commands.forEach(function (command) {
        directions[command](current);
    });

    code += codeMap[current.x + ',' + current.y];
});

console.log('code', code);
